import json
import random

def generate_unique_title(titles):
    if not titles:
        return None
    selected_title = random.choice(titles)
    titles.remove(selected_title)
    return selected_title

# Hàm để tạo một genres ngẫu nhiên
def generate_genres():
    genres = ["Action", "Adventure", "Sci-Fi", "Drama", "Crime", "Thriller", "Fantasy", "Cartoon"]
    return random.sample(genres, random.randint(1, 3))

# Hàm để tạo một release_date ngẫu nhiên
def generate_release_date():
    return random.randint(2000, 2024)

