# Tạo dữ liệu ngẫu nhiên cho mỗi tài liệu
data = []
for i in range(1, 99):
    document = {
        "id": i,
        "title": generate_unique_title(list_of_titles.copy()),  # Truyền một bản sao của danh sách tiêu đề
        "genres": generate_genres(),
        "release_date": generate_release_date()
    }
    data.append(document)

# Đường dẫn tới tệp JSON
